dictdataeng= {v: k for k, v in dictdata.items()}
def en2th_text(data):
    ''' return en -> th mapping '''
    data = list(data)
    data_out = ""
    for letter in data:
        try:
            letter = dictdata[letter]
        except:
            letter = letter
        data_out+=letter
    return data_out


def enth_main(text):
    return(en2th_text(text))

